#!/usr/bin/python3__author__ = 'cetauri'import osimport shutilimport jsonimport loggingfrom io import StringIOimport logging.configimport sysimport reimport datetimelogging.config.fileConfig('logging.conf')# create loggerlogger = logging.getLogger('simpleExample')def configLoad():    """    환경 설정 로드    """    content = open("sexy.json").read()    stringIO = StringIO(content)    return json.load(stringIO)def libraryRename(globalConfig):    suffix = globalConfig["suffix"]    libPath = globalConfig["libPath"]    libs = globalConfig["libs"]    for f in libs:        source = libPath + f + ".js"        target = libPath + f + suffix + ".js"        if os.path.exists(source):            shutil.move(source, target)            print(f + " move " + target)def rewriteGYP(globalConfig):    """    node.gyp library_files rewrite    :param globalConfig:    """    gyp = globalConfig["gyp"]    suffix = globalConfig["suffix"]    old_gyp = ""    for line in open(gyp["path"]):        old_gyp += line    # get only lib list    start = old_gyp.find(gyp["prefix"])    end = old_gyp.find(gyp["suffix"], start)    libList = list(old_gyp)[start:end]    libText = "".join(libList).split("\n")    # rewrite library_files    new_gyp = ""    delimiter = "'lib/"    count = 0    for line in libText:        func_name = line.replace(delimiter, "").replace(".js',", "")        if func_name.strip() in globalConfig["libs"]:            s = line.find(delimiter)            e = line.find("',", s)            func = list(line)[s + len(delimiter):e]            func = "".join(func)            func = os.path.splitext(func)            # XXX TODO 구려            if count == 2:                new_gyp += "      'lib/sexy.js',\n"            new_gyp += "      'lib/" + func[0] + suffix + func[1] + "',\n"        else:            new_gyp += line + "\n"        # 구려        count += 1    # save    gypList = list(old_gyp)    output = open(gyp["path"], "w")    output.write("".join(gypList[0:start]) + new_gyp + "".join(gypList[end:]))    output.close()def markVersion(globalConfig):    """    :param globalConfig:    :rtype : object    """    sexyConfig = globalConfig["sexy"]    version = sexyConfig["version"] + " (build. " + str(datetime.datetime.now()) + ") \\n " + globalConfig["suffix"]    version_string = sexyConfig["version_string"]    path = sexyConfig["path"]    new_node_version = ""    node_version = open(path)    for line in node_version:        if line.startswith(version_string):            new_node_version += version_string + " \"\\nSexy.js v" + version + "\"\n"        else:            new_node_version += line    output = open(path, "w")    output.write(new_node_version)    output.close()    # print(new_node_version)def rewriteGlobalVars(globalConfig):    """    NativeModule.wrapper 에 \n 을 넣으면 에러 메시지에  suffix 노출을 막을 수 있다    """    logging.info("# " + sys._getframe().f_code.co_name + " start")    node_path = globalConfig["global"]["path"]    node_code = open(node_path).read()    suffix = globalConfig["suffix"]    for _var in globalConfig["global"]["list"]:        replaceVar = _var + suffix        if not node_code.__contains__(replaceVar):            node_code = node_code.replace(_var, replaceVar)            logging.info(replaceVar)    output = open(node_path, "w")    output.write(node_code)    output.close()    logging.info("# " + sys._getframe().f_code.co_name + " finish")def reRequireLibrary(globalConfig):    suffix = globalConfig["suffix"]    libs = globalConfig["libs"]    globalVars = globalConfig["global"]["list"]    # require(', ') 사이의 값    # ",' 가능    # 전후방 탐색으로 원하는 결과만    express = '(?<=require\(["\'])(' + "|".join(libs) + '?)(?=["\']\))'    regexp = re.compile(express)    count = 0    for root, dirs, files in os.walk("node"):        for file in files:            if file.endswith(".js"):                jsFile = root + "/" + file                # print(jsFile)                try:                    jsSource = open(jsFile).read()                    if regexp.search(jsSource):                        freq = len(regexp.findall(jsSource))                        count += freq                        if not jsSource.__contains__(libs[0]+suffix):                            jsSource = re.sub(express, "\\1" + suffix, jsSource)                            # output = open(jsFile, "w")                            # output.write(source)                            # output.close()                    # __filename, __dirname replace                    for _var in globalVars:                        if jsFile.endswith("src/node.js"):                            continue                        replaceVar = _var + suffix                        if not jsSource.__contains__(replaceVar):                            jsSource = jsSource.replace(_var, replaceVar)                            # print(jsFile)                    output = open(jsFile, "w")                    output.write(jsSource)                    output.close()                except UnicodeDecodeError as e:                    if file != "parser-high-byte-character.js":                        assert str(e) == "", str(e) + " (" + jsFile + ")"                except BaseException as e:                    print(str(e))                    assert str(e) == "", "Unknown Exception"    # print(count)def rewriteNativeModule(globalConfig):    suffix = globalConfig["suffix"]    libs = globalConfig["libs"]    node_path = globalConfig["global"]["path"]    node_code = open(node_path).read()    # reRequireLibrary에 정규식 설명 있음    express = '(?<=NativeModule.require\(["\'])(' + "|".join(libs) + '?)(?=["\']\))'    source = re.sub(express, "\\1" + suffix, node_code)    output = open(node_path, "w")    output.write(source)    output.close()def createNewLibrary(globalConfig):    suffix = globalConfig["suffix"]    sexyConfig = globalConfig["sexy"]    sourcePath = "lib/sexy.js"    targetPath = "node/" + sourcePath    # 파일 복사    shutil.copy(sourcePath, targetPath)    # replace    source = open(sourcePath).read()    source = source.replace("${suffix}", suffix)    source = source.replace("${version}", sexyConfig["version"])    output = open(targetPath, "w")    output.write(source)    output.close()    # gyb 추가def main():    """    build    """    globalConfig = configLoad()    createNewLibrary(globalConfig)    libraryRename(globalConfig)    rewriteGYP(globalConfig)    rewriteGlobalVars(globalConfig)    markVersion(globalConfig)    reRequireLibrary(globalConfig)    rewriteNativeModule(globalConfig)    # #TODO loggingif __name__ == '__main__':    main()